}

@layer utilities {
  .glass-card {
    background: rgba(255, 255, 255, 0.08);
    backdrop-filter: blur(15px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    box-shadow: 0 4px 20px rgba(0, 150, 255, 0.1);
  }

  .neon-button {
    background: linear-gradient(45deg, hsl(200, 100%, 55%), hsl(270, 100%, 75%), hsl(320, 100%, 65%));
    background-size: 300% 300%;
    animation: gradient 2.5s ease infinite;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 150, 255, 0.3);
  }

  .neon-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 35px rgba(0, 150, 255, 0.6), 0 0 20px rgba(139, 92, 246, 0.4);
    background-size: 200% 200%;
  }

  .custom-cursor {
    position: fixed;
    width: 20px;
    height: 20px;
    background: linear-gradient(45deg, hsl(210, 100%, 50%), hsl(280, 100%, 70%));
    border-radius: 50%;
    pointer-events: none;
    z-index: 10000;
    transition: transform 0.1s ease;
    box-shadow: 0 0 20px rgba(0, 212, 255, 0.6);
  }

  .custom-cursor-trail {
    position: fixed;
    width: 8px;
    height: 8px;
    background: rgba(139, 92, 246, 0.6);
    border-radius: 50%;
    pointer-events: none;
    z-index: 9999;
  }

  .particle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: linear-gradient(45deg, hsl(210, 100%, 50%), hsl(310, 100%, 70%));
    border-radius: 50%;
    animation: particle linear infinite;
  }

  .glitch-text {
    animation: glitch 2s infinite;
  }

  .cube-3d {
    transform-style: preserve-3d;
    animation: rotate3d 10s linear infinite;
  }

  .parallax-bg {
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
  }

  .slider-smooth .slider-track {
    transition: all 0.2s ease-out;
  }

  .slider-smooth .slider-thumb {
    transition: all 0.15s ease-out;
    box-shadow: 0 2px 10px rgba(0, 150, 255, 0.3);
  }

  .slider-smooth .slider-thumb:hover {
    transform: scale(1.1);
    box-shadow: 0 4px 20px rgba(0, 150, 255, 0.5);
  }
}

@keyframes gradient {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

@keyframes glitch {
  0%, 100% { transform: translate(0); }
  20% { transform: translate(-2px, 2px); }
  40% { transform: translate(-2px, -2px); }
  60% { transform: translate(2px, 2px); }
  80% { transform: translate(2px, -2px); }
}

@keyframes particle {
  0% { transform: translateY(100vh) rotate(0deg); opacity: 0; }
  10% { opacity: 1; }
  90% { opacity: 1; }
  100% { transform: translateY(-100vh) rotate(360deg); opacity: 0; }
}

@keyframes rotate3d {
  0% { transform: rotateX(0deg) rotateY(0deg); }
  100% { transform: rotateX(360deg) rotateY(360deg); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes glow {
  from { box-shadow: 0 0 20px rgba(0, 212, 255, 0.5); }
  to { box-shadow: 0 0 30px rgba(139, 92, 246, 0.8); }
}

